from .base_page import BasePagefrom selenium.webdriver.common.by import Byfrom .locators import ProductPageLocatorsclass ProductPage(BasePage):    def open(self):        super().open()        self.product_name = self.browser.find_element(*ProductPageLocators.PRODUCT_NAME).text        self.product_price = self.browser.find_element(*ProductPageLocators.PRODUCT_PRICE).text        def add_product_to_basket(self):        btn_add_to_basket = self.browser.find_element(*ProductPageLocators.BASKET_BUTTON)        btn_add_to_basket.click()    def should_be_alert_success(self):        assert self.is_element_present(*MainPageLocators.ALERT_SUCCESS), "Alert success is not presented"    def should_be_equal_names(self):        assert self.product_name == self.browser.find_element(*ProductPageLocators.PRODUCT_NAME_IN_BASKET).text, "names not equals"    def should_be_equal_prices(self):        assert self.product_price == self.browser.find_element(*ProductPageLocators.BASKET_PRICE).text, "prices not equals"    def should_not_be_success_message(self):        assert self.is_not_element_present(*ProductPageLocators.ALERT_SUCCESS), "Success message is presented, but should not be"            def should_is_disappeared_success_message(self):        assert self.is_disappeared(*ProductPageLocators.ALERT_SUCCESS), "Success message is presented, but should not be"